import  { faBank, faHeart } from "@fortawesome/free-solid-svg-icons"
import { render } from "@testing-library/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { Component } from "react";

class FavoriteButton extends Component{

showMessage = () => {
    alert("Added to your favorites!", {cancelable: false});
};

removeMessage = () => {
    alert("Removed from your favorites");

};

state = { liked: false};

toggle = () => {
    let localLiked = this.state.liked;
    localLiked = !localLiked;
    this.setState({liked: localLiked});
};

render() {
    return(
        <div className="flex justify-between pt-1" onClick={() => this.toggle()}>

            {this.state.liked === false ? (<FontAwesomeIcon icon = {faHeart} />)
            : (<FontAwesomeIcon icon = {faHeart} transform="grow-4" color="red"/>)(this.showMessage)}
        </div>
    );
}
}

export default FavoriteButton;
